mapper.select:
SELECT * FROM activity

	
		AND ID = #{example.id}
	
	
		AND des = #{example.des}
				
	
		AND name = #{example.name}
	
	
		AND icon = #{example.icon}
	
	
		AND `type` = #{example.type}
			
	
		AND detailType = #{example.detailType}
							
	
		AND `order` = #{example.order}
	
	
		AND activity_type = #{example.activityType}
				
	
		AND extendShowTime = #{example.extendShowTime}
	
	
		AND alwaysShow = #{example.alwaysShow}
	
	
		AND content = #{example.content}
	
	
		AND `level` = #{example.level}
	
	
		AND days = #{example.days}
						
	
		AND `time` = #{example.time}
				
	
		AND des = #{example.des}
	
	
		AND times = #{example.times}
	
	
		AND reward = #{example.reward}
	
	
		AND details = #{example.details}
	
	
		AND mapId = #{example.mapId}
	
	
		AND args = #{example.args}
	
	
		and `state` = #{example.state}
	
	
		and type_desc = #{example.typeDesc}
	
	
		and server_id = #{example.serverId}
	

order by (id+0)
