mapper.select:
  	
	SELECT * FROM gm_platform
	 
		
		and kind_id = #{example.kindId}
		
		
		and platform_id = #{example.platformId}

  	
	SELECT * FROM gm_platform
	 
		
		and platform_id = #{example.platformId}
		
		
		and kind_id = #{example.kindId}
		
		
		and platform = #{example.platform}
		
		
		and platform_name = #{example.platformName}

mapper.insert:
insert into gm_platform (platform_id, kind_id, platform, platform_name
  )
values (#{platformId,jdbcType=INTEGER}, #{kindId,jdbcType=INTEGER}, #{platform,jdbcType=VARCHAR}, #{platformName,jdbcType=VARCHAR}
  )

insert into gm_platform
platform_id, kind_id, platform, platform_name
#{platformId,jdbcType=INTEGER}, #{kindId,jdbcType=INTEGER}, #{platform,jdbcType=VARCHAR}, #{platformName,jdbcType=VARCHAR}

mapper.update:
update gm_platform
set platform_name = #{platformName,jdbcType=VARCHAR},
platform = #{platform,jdbcType=VARCHAR},
kind_id = #{kindId,jdbcType=INTEGER}
where platform_id = #{platformId,jdbcType=INTEGER}
